import Daytracker from "../components/daytracker";
import ExerciseContainer from "../components/exerciseContainer";
import { Fragment, useEffect, useState } from "react";

function Exercises() {
    const [exercises, setExercises] = useState([]);

    const [selectedOption, setSelectedOption] = useState('');
    const [values, setValue] = useState(0);

    const handleSelectChange = (event) => {
        setSelectedOption(event.target.value);
    };

    const getExercises = async () => {
        try {
            const response = await fetch("http://localhost:5001/exercises");
            const jsonData = await response.json();
            setExercises(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getExercises();
    }, []);

    return ( 
        <Fragment>
            <Daytracker />
            <div className="allExerciseContainer" style={{marginTop: '150px', width: '100%'}}>
            <div className="selectContainer">
                <label>
                    Wähl den Muskel, den du trainieren willst:
                    <select name="selectedFruit" value={selectedOption} onChange={handleSelectChange}>
                        <option value="option1">Brust</option>
                        <option value="option2">Rücken</option>
                        <option value="option3">Beine</option>
                        <option value="option4">Schultern</option>
                        <option value="option5">Bizeps</option>
                        <option value="option6">Trizeps</option>
                    </select>
                </label>
            </div>
                <div style={{display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '10px'}}>
                    {exercises.map((exercise) => (
                        <ExerciseContainer
                        key={exercise.id}
                        name={exercise.name}
                        muscle={exercise.muscle}
                        type={exercise.type}
                        description={exercise.description}
                        />
                ))}
                </div>
            </div>
        </Fragment>
     );
}

