import './GetData.css'
import { useState } from 'react';

function GetData() {
    const [formData, setFormData] = useState({
        prename: '',
        general_goal: '',
        height_cm: '',
        gender: '',
        weight: '',
        weight_goal: ''
    });

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:3000/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const result = await response.json();
                console.log(result.message); // Erfolgsnachricht vom Server
            } else {
                throw new Error('Fehler beim Senden der Daten.');
            }
        } catch (error) {
            console.error(error.message);
        }
    };

    return(
        <div className='GetDataContainer'>
            <h1 className='welcomeHeader'>Willkommen bei unserem Techacademy Projekt!</h1>
            <h1 className='welcomeHeader'>Erzähl uns etwas über dich:</h1>
            <form onSubmit={handleSubmit}>
                <div className='dataSectionContainer' style={{marginBottom: '50px'}}>
                    <h1>Allgemein</h1>
                    <div className='dataInputContainer'>
                        <label>Vorname: </label>
                        <input
                        type="text"
                        placeholder="Vorname..."
                        name="name"
                        value={formData.prename}
                        onChange={handleChange}
                        />
                    </div>
                    <div className='dataInputContainer'>
                        <label>Ziel:</label>
                        <select>
                            <option>Abnehmen</option>
                            <option>Zunehmen</option>
                            <option>Gesünder leben</option>
                            <option>Fitter werden</option>
                        </select>
                    </div>
                    <div className='dataInputContainer'>
                        <label>Größe: </label>
                        <input type="number" placeholder="in cm..." />
                    </div>
                    <div className='dataInputContainer'>
                        <label>Aktuelles Gewicht: </label>
                        <input type="number" placeholder="in kg..." />
                    </div>
                    <div className='dataInputContainer'>
                        <label>Zielgewicht: </label>
                        <input type="number" placeholder="falls gewünscht" />
                    </div>
                    <div className='dataInputContainer'>
                        <label>Geschlecht:</label>
                        <select>
                            <option>Männlich</option>
                            <option>Weiblich</option>
                        </select>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default GetData;
