import './GetData.css'
import { useState } from 'react';

function GetData() {
    const [formData, setFormData] = useState({
        prename: '',
        general_goal: '',
        height_cm: '',
        gender: '',
        weight: '',
        weight_goal: ''
    });

const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
        ...formData,
        [name]: value,
    });
};

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:3000/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const result = await response.json();
                console.log(result.message); // Erfolgsnachricht vom Server
            } else {
                throw new Error('Fehler beim Senden der Daten.');
            }
        } catch (error) {
            console.error(error.message);
        }
    };

    return (
        <div className='GetDataContainer'>
            {/* ... (vorhandener Code bleibt unverändert) */}
            <form onSubmit={handleSubmit}>
                {/* ... (vorhandener Code bleibt unverändert) */}
                <div className='dataInputContainer'>
                    <label>Vorname: </label>
                    <input
                        type="text"
                        placeholder="Vorname..."
                        name="prename"
                        value={formData.prename}
                        onChange={handleChange}
                    />
                </div>
                <div className='dataInputContainer'>
                    <label>Ziel:</label>
                    <select
                        name="general_goal"
                        value={formData.general_goal}
                        onChange={handleChange}
                    >
                        <option>Abnehmen</option>
                        <option>Zunehmen</option>
                        <option>Gesünder leben</option>
                        <option>Fitter werden</option>
                    </select>
                </div>
                {/* ... Weitere Input- und Select-Felder mit onChange-Handler ... */}
                <button type="submit">Jetzt loslegen</button>
            </form>
        </div>
    );
}


export default GetData;
