import React, { useState } from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';

function MeinSidebar() {
  const [showSidebar, setShowSidebar] = useState(false);

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="#home">Meine App</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" onClick={toggleSidebar} />
        </Container>
      </Navbar>

      <div className={`sidebar ${showSidebar ? 'show' : ''}`}>
        <Nav className="flex-column">
          <Nav.Link href="#home">Home</Nav.Link>
          <Nav.Link href="#link">Link</Nav.Link>
          {/* Weitere Nav.Link-Komponenten hier */}
        </Nav>
      </div>

      <style jsx>{`
        .sidebar {
          position: fixed;
          top: 0;
          right: -250px; /* Startposition außerhalb des Bildschirms */
          height: 100%;
          width: 250px;
          background-color: #f8f9fa;
          transition: right 0.3s ease-in-out;
        }

        .sidebar.show {
          right: 0; /* Sidebar wird eingeblendet */
        }
      `}</style>
    </div>
  );
}

export default MeinSidebar;
