import { Fragment, useEffect, useState } from 'react';
import './Settings.css';

function Settings() {
  const [userData, setUserData] = useState([]);
  const [selectedUser, setSelectedUser] = useState("");
  const [selectedUserInfo, setSelectedUserInfo] = useState({});

  const getUserData = async () => {
    try {
      const response = await fetch("http://localhost:5001/data");
      const jsonData = await response.json();
      setUserData(jsonData);
      console.log(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  const handleSelect = async (event) => {
    const selectedUserName = event.target.value;
    setSelectedUser(selectedUserName);

    try {
      const response = await fetch(`http://localhost:5001/data/${selectedUserName}`);
      const jsonData = await response.json();
      setSelectedUserInfo(jsonData); // Setze die ausgewählten Benutzerinformationen
      console.log(jsonData);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getUserData();
  }, []);

  return ( 
    <Fragment>
      <div className='settingContainer'>
        <label>Wähle den User aus:</label>
        <select onChange={handleSelect} value={selectedUser}>
          <option value="">Wer bist du?</option>
          {userData.map((user) => (
            <option key={user.user_id} value={user.user_name}>
              {user.user_name}
            </option>
          ))}
        </select>
        <div className='userInformation'>
          {selectedUserInfo && (
            <Fragment>
              <p>Name: {selectedUserInfo.user_name}</p>
              <p>Ziel: {selectedUserInfo.goal}</p>
              <p>Größe: {selectedUserInfo.height_cm}</p>
              <p>Gewicht: {selectedUserInfo.current_weight}</p>
              <p>Zielgewicht: {selectedUserInfo.weight_goal}</p>
              <p>Geschlecht: {selectedUserInfo.gender}</p>
            </Fragment>
          )}
        </div>
      </div>
    </Fragment>
  );
}

export default Settings;
