import { Fragment, useEffect, useState } from 'react';

function Settings() {

    const [userData, setUserData] = useState([]);
    const [selectedUser, setSelectedUser] = ("");

    const getUserData = async () => {
        try {
            const response = await fetch("http://localhost:5001/data");
            const jsonData = await response.json();
            setUserData(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    const handleChange = async (event) => {
        const selectedUserName = event.target.value;
        setSelectedUser(selectedUserName);

        try {
            const response = await fetch(`http://localhost:5001/data/${selectedUserName}`);
            const jsonData = await response.json();
            console.log(jsonData);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getUserData();
    }, []);

    return ( 
        <Fragment>
            <div>
                <select onChange={handleChange} value={selectedUser}>
                    <option value="">Wer bist du?</option>
                    {userData.map((user) => (
                        <option key={user.user_id} value={user.user_name}>{user.user_name}</option>
                    ))}
                </select>
            </div>
        </Fragment>
    )
}

export default Settings;
